mapper.insert:

  INSERT INTO TB_BOARD
		( IDX
		  , TITLE
		  , CONTENTS
		  , COUNT
		  , WRITER
		  , INDATE )
	VALUES ( NULL
		  , #{title, jdbcType=VARCHAR}
		  , #{contents, jdbcType=VARCHAR}
		  , #{count}
		  , #{writer, jdbcType=VARCHAR}
		  , SYSDATE() )

mapper.update:
UPDATE tb_board
SET TITLE=#{title}
	, CONTENTS=#{contents}
	WHERE IDX=#{idx}

mapper.select:
SELECT `name`
FROM tb_user
WHERE userID = #{userID}
AND	userPw = #{userPw}
